mapper.select:
SELECT COUNT(*) AS CNT
FROM t3_tbl_user
WHERE USER_EMAIL = #{email}

SELECT COUNT(*) AS CNT
FROM t3_tbl_user
WHERE NICK = #{nick}

SELECT COUNT(*) AS CNT
FROM t3_tbl_user
WHERE PHONE = #{phone}

SELECT COUNT(*) AS CNT
FROM t3_tbl_user
WHERE NICK = #{nick} AND USER_NO != #{no}

SELECT COUNT(*) AS CNT
FROM t3_tbl_user
WHERE PHONE = #{phone} AND USER_NO != #{no}

mapper.update:
UPDATE t3_tbl_user
SET
	SMSYN = #{smsYn}
WHERE USER_NO = #{no}

UPDATE t3_tbl_user
SET
	USER_PWD = #{pwd}
WHERE USER_NO = #{no}
