mapper.select:
SELECT * FROM t3_tbl_board B LEFT JOIN ( SELECT COUNT(*) COMM_CNT, BOARD_NO FROM t3_tbl_board_comment GROUP BY BOARD_NO ) C ON B.BOARD_NO = C.BOARD_NO WHERE 1=1
AND (TITLE LIKE CONCAT('%', #{keyword}, '%') OR NICK LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY B.BOARD_NO DESC LIMIT #{startNum}, #{lastNum}
